mapper.select:
SELECT MAIL_NO AS mailNo, SENDER AS sendMemNo, TITLE AS title, SEND_DATE AS sendDate, to_char(SEND_DATE,'mm/dd') AS sendDateFormat, RECEIVER AS recMemNo, READ_YN AS readYN
FROM (SELECT ROW_NUMBER() OVER(ORDER BY ML.MAIL_NO DESC) AS ROW_NUM, ML.MAIL_NO, ML.MEM_NO SENDER, ML.TITLE, ML.SEND_DATE, MR.MEM_NO RECEIVER, MR.READ_YN, MR.DEL_YN FROM MAIL ML JOIN MAIL_RECEIVE MR ON ML.MAIL_NO = MR.MAIL_NO JOIN MEMBER M ON ML.MEM_NO = M.MEM_NO WHERE MR.MEM_NO = #{memNo} AND MR.DEL_YN = 'N')
WHERE ROW_NUM BETWEEN #{startPage} AND #{endPage}
